  }
  
  static getInitialProps({ pathname }){
    this.setState({pathname: pathname})
    return { pathname }
  }
  handleClick = e => {
    e.preventDefault()
    router.push(href)
  }
  
  render() {
    const router = this.props.pathname
    const active = router.pathname === this.props.href ? true : false
    return <a href={this.props.href} onClick={this.handleClick} className="effect-slash">
      <div className="effect-inner">
        <span className="effect-l"><span>Home</span></span>
        <span className="effect-r"><span>Home</span></span>
        <span className="effect-shade"><span>Home</span></span>
      </div>
    </a>
  }
}
export default EffectSlash
