import React, { Component } from 'react'
import { useRouter } from 'next/router'

class EffectSlash extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      pathname: '/punishment'
    }
    router = useRouter()
  }
  
  handleClick = e => {
    e.preventDefault()
    router.push(href)
  }
  
  render() {
    const {href, title} = this.props
    return <a href={this.props.href} onClick={this.handleClick} className="effect-slash" active={this.props.active}>
      <div className="effect-inner">
        <span className="effect-l"><span>{title}</span></span>
        <span className="effect-r"><span>{title}</span></span>
        <span className="effect-shade"><span>{title}</span></span>
      </div>
    </a>
  }
}

export default EffectSlash
