import React, { Component } from 'react'

class EffectSlash extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      pathname: '/punishment'
    }
  }
  
  static getInitialProps({ pathname }){
    this.setState({pathname: pathname})
    console.log(pathname)
    return { pathname }
  }
  handleClick = e => {
    e.preventDefault()
    router.push(href)
  }
  
  render() {
    console.log(this.state.pathname)
    const router = this.props.pathname
    const {href, title} = this.props
    const active = this.state.pathname === href ? "true" : "false"
    return <a href={this.props.href} onClick={this.handleClick} className="effect-slash" active={active}>
      <div className="effect-inner">
        <span className="effect-l"><span>{title}</span></span>
        <span className="effect-r"><span>{title}</span></span>
        <span className="effect-shade"><span>{title}</span></span>
      </div>
    </a>
  }
}

export default EffectSlash
