import React from 'react'
import EffectSlash from './modules/effectslash.module'

class Header extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      menuOpen: false
    }
  }
  handleMenu(e) { this.setState({menuOpen: !this.state.menuOpen}) }
  componentDidMount() {
    window.addEventListener("mousemove", this.cursor)
  }
  cursor = (e) => {
    const cursor = document.querySelector('.custom-cursor-sword')
    cursor.style.transform = 'matrix(1, 0, 0, 1, '+ (e.pageX) +', '+ (e.pageY - window.scrollY) +')'
  }
  render() {
    return (
      <header id="header">
        <div className="header">
          <div className="container">
            <div className="row">
              <div className="col-12">
                <nav className={"navbar" + (this.state.menuOpen ? " responsive-active" : "")}>
                  <ul className="navbar-nav">
                    <li className="nonavstyle"><a href="/discord"><i className="fab fa-discord fa-lg"></i></a></li>
                    <li className="nonavstyle" style={marginRight, 20 +"px"}><a href="/discord"><i className="fab fa-facebook-square fa-lg"></i></a></li>
                    <li><EffectSlash href="/community" title="Community" /></li>
                    <li><EffectSlash href="/events" title="Events" /></li>
                    <li><EffectSlash href="/resources" title="Resources" /></li>
                    <li className="logo-wrap">
                      <a href="/" className="logo">
                        <h1 className="logo-text">UTLRACRAFT</h1>
                      </a>
                    </li>
                    <li><EffectSlash href="/rules" title="Bans" /></li>
                    <li><EffectSlash href="/support" title="Support" /></li>
                    <li><EffectSlash href="/store" title="Store" /></li>
                  </ul>
                </nav>
              </div>
            </div>
          </div>
        </div>
      </header>
    )
  }
}
export default Header
