    const router = this.props.pathname
    const active = this.state.pathname === this.props.href ? "true" : "false"
    return <li active={active}>
      <a href={this.props.href} onClick={this.handleClick} className="effect-slash">
        <div className="effect-inner">
          <span className="effect-l"><span>Home</span></span>
          <span className="effect-r"><span>Home</span></span>
          <span className="effect-shade"><span>Home</span></span>
        </div>
      </a>
    </li>
  }
}
EffectSlash.getInitialProps = async ({ req }) => {
  let protocol = 'https:'
  let host = req ? req.headers.host : window.location.hostname
  if (host.indexOf('localhost') > -1) {
    protocol = 'http:'
  }

  const jobs = await (
    await fetch(`${protocol}//${host}/api/allJobs.js`)
  ).json()

  return {
    jobs
  }
}
export default EffectSlash
